-- This file will create the languages table, and then insert some rows into it.

DROP TABLE IF EXISTS languages; --Nuke the table so we can easily re-seed the DB

CREATE TABLE languages (
    id INTEGER PRIMARY KEY AUTOINCREMENT, -- assigns IDs for us and keeps track of htem
    name TEXT,
    family TEXT,
    countries TEXT,
    description TEXT,
    difficulty INTEGER,
    alive BOOLEAN,
    image_url TEXT
);

